import useInput from "../hooks/use-input";
const BasicForm = (props) => {

  const {
    value: enteredFirstName,
    isValid : enteredFirstNameIsValid,
    hasError: hasFirstNameError,
    valueChangeHandler: firstnameChangeHandler,
    inputBlurHandler: firstnameBlurHandler,
    reset : resetfirstName
  } = useInput(value => value.trim() !== '');

  const {
    value: enteredLastName,
    isValid : enteredLastNameIsValid,
    hasError: hasLastNameError,
    valueChangeHandler: lastnameChangeHandler,
    inputBlurHandler: lastnameBlurHandler,
    reset : resetlastName
  } = useInput(value => value.trim() !== '');


  const {
    value: enteredEmail,
    isValid : enteredEmailIsValid,
    hasError: hasEmailError,
    valueChangeHandler: emailChangeHandler,
    inputBlurHandler: emailBlurHandler,
    reset : resetEmail
  } = useInput(value => value.includes("@"));

  let formIsValid = false;

  if(enteredFirstNameIsValid && enteredLastNameIsValid && enteredEmailIsValid){
    formIsValid = true;
  }
  const formSubmitHandler = () =>{
    if(!enteredEmailIsValid && !enteredFirstNameIsValid && !enteredLastNameIsValid){
      return;
    }
    resetfirstName();
    resetlastName();
    resetEmail();

  }
  const firstnameInputClasses = hasFirstNameError
    ? "form-control invalid"
    : "form-control";
  const lastnameInputClasses = hasLastNameError
    ? "form-control invalid"
    : "form-control";

  const emailInputClasses = hasEmailError
    ? "form-control invalid"
    : "form-control";



  return (
    <form onSubmit={formSubmitHandler}>
      <div className='control-group'>
        <div className={firstnameInputClasses}>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' onChange={firstnameChangeHandler} onBlur={firstnameBlurHandler} value={enteredFirstName}/>
        </div>
        <div className={lastnameInputClasses}>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' onChange={lastnameChangeHandler} onBlur={lastnameBlurHandler} value={enteredLastName}/>
        </div>
      </div>
      <div className={emai}>
        <label htmlFor='email'>E-Mail Address</label>
        <input type='email' id='name' onChange={emailChangeHandler} onBlur={emailBlurHandler} value={enteredEmail}/>
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
